var url;

url = "https://nuforc.org/subndx/?id=";

async function scrapeH1Text(url) {
    try {
        // Fetch the HTML of the webpage
        const response = await fetch(url);
        const html = await response.text();

        // Parse the HTML
        const parser = new DOMParser();
        const doc = parser.parseFromString(html, 'text/html');

        // Get the div with the specific ID and class
        const div = doc.querySelector('div#primary.content-area.clr');

        // Get the h1 element within the div
        const h1 = div.querySelector('h1');

        // Return the text content of the h1 element
        return h1.textContent;
    } catch (err) {
        console.error('Failed to scrape page: ', err);
    }
}

// Usage
scrapeH1Text('https://example.com')
    .then(text => console.log(text))
    .catch(err => console.error(err));
